import { useState } from 'react';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState("");
  const [output, setOutput] = useState("");

  const handleQuery = async () => {
    const response = await fetch("http://localhost:8080/api/v1/llm/chat", {
      method: "POST",
      body: JSON.stringify({"query":prompt})
    });
    const finalOutput = await response.json();
    setOutput(finalOutput);
  }

  return (
    <div className="App">
      <div>
        <input value={prompt} onChange={e => setPrompt(e.target.value)} placeholder='message...' />
        <button onClick={handleQuery}>Go</button>
      </div>

      <div>
        <p>{output}</p>
      </div>
    </div>
  );
}

export default App;
